mapper.update:
UPDATE COMPANY
SET CMSTATE = #{cmstate}
WHERE CMCODE = #{cmcode}

UPDATE COMPANY
SET CMNAME = #{cmname}, CMADDRESS = #{cmaddress}, CMTEL = #{cmtel}, CMLINK= #{cmlink}, CMIMG=#{cmimg}
WHERE CMCODE = #{cmcode}

UPDATE CAFE
SET CFSTATE = #{cfstate}
WHERE CFCODE = #{cfcode}

UPDATE CAFE
SET CFNAME = #{cfname}, CFADDRESS = #{cfaddress}, CFTEL = #{cftel}, CFLINK= #{cflink}, CFIMG=#{cfimg}, CFSIGIMG = #{cfsigimg}
WHERE CFCODE = #{cfcode}
