mapper.select:
select *
from CAFE
where CFNUM between #{start} and #{end}

mapper.update:
UPDATE CAFE
SET CFNAME= #{cfname}, CFTEL=#{cftel}, CFLINK=#{cflink},CFIMG=#{cfimg},CFSIGIMG=#{cfsigimg}, CFADDRESS=#{cfaddress}
WHERE CFMID = #{cfmid}

mapper.select:
select CFIMG
from CAFE
where CFMID = #{cfmid}

select CFSIGIMG
FROM CAFE
WHERE CFMID = #{cfmid}
