UPDATE COMPANY
SET CMNAME= #{cmname}, CMTEL=#{cmtel}, CMLINK=#{cmlink},CMIMG=#{cmimg},
CMADDRESS=#{cmaddress}
WHERE CMMID = #{cmmid}

