mapper.update:
update PRODUCT
set PDSTATE = #{pdstate}
where PDCODE = #{pdcode}

update PRODUCT
set PDSTATE = #{pdstate}, PDNAME = #{pdname}, PDPRICE = #{pdprice},
PDTYPE = #{pdtype}, PDIMG = #{pdimg}, PDAMOUNT = #{pdamount}
where PDCODE = #{pdcode}

mapper.select:
SELECT PDIMG
FROM PRODUCT
WHERE PDCODE = #{pdcode}

select p.*
from (select rownum rn, product.* from PRODUCT where pdtype = #{type}) p
where p.rn between #{start} and #{end}

select p.*
from (select rownum rn, product.* from PRODUCT) p
where p.rn between #{start} and #{end}

select p.*
from (select rownum rn, product.* from PRODUCT where pdtype = #{pdtype}) p
where p.rn between #{start} and #{end}
